package com.github.lette1394.fractional.expression

import io.kotest.core.spec.style.FreeSpec
import io.kotest.matchers.shouldBe

class FitsTest : FreeSpec({
    "width" - {
        "1x1" {
            val target = """
                x
            """.trimIndent()

            val base = """
                y
            """.trimIndent()

            val expected = """
                x
            """.trimIndent()

            Fits.width(target, base) shouldBe expected
        }

        "1x2" {
            val target = """
                x
            """.trimIndent()

            val base = """
                y
                y
            """.trimIndent()

            val expected = """
                 
                x
            """.trimIndent()

            Fits.width(target, base) shouldBe expected
        }

        "1x2 reverse" {
            val target = """
                x
                x
            """.trimIndent()

            val base = """
                y
            """.trimIndent()

            val expected = """
                x
                x
            """.trimIndent()

            Fits.width(target, base) shouldBe expected
        }

        "2x2" {
            val target = """
                x
                x
            """.trimIndent()

            val base = """
                y
                y
            """.trimIndent()

            val expected = """
                x
                x
            """.trimIndent()

            Fits.width(target, base) shouldBe expected
        }
    }
})
